import React from 'react';
import { useLocation } from 'react-router-dom';

const Score = () => {
    const location = useLocation();
    const { score, userId, userAnswers, questions } = location.state || { score: 0, user_id: null, userAnswers: {}, questions: [] };

    // Calculate correct answers
    const correctAnswersCount = questions.filter((question) => 
        question.correctAnswer === userAnswers[question.id]
    ).length;

    // Prepare feedback message
    let feedbackMessage = '';
    if (score >= 7) { // Assuming score is out of 10
        feedbackMessage = 'Great job! Keep up the good work!';
    } else {
        feedbackMessage = "Don't worry! Practice makes perfect!";
    }

    return (
        <div>
            <h2>Your Score: {score}</h2>
            <p>User ID: {userId}</p>
            <p>Correct Answers: {score} out of {questions.length}</p>
            <p>{feedbackMessage}</p>
        </div>
    );
};

export default Score;
