import React, { useEffect, useState, useContext, useCallback } from 'react';
import { getQuestions } from '../api'; // Import getQuestions function
import { useNavigate } from 'react-router-dom'; // Import useNavigate for navigation
import { UserContext } from '../context/UserContext'; // Import your UserContext

const Test = () => {
    const { currentUser } = useContext(UserContext); // Access currentUser from context
    const [questions, setQuestions] = useState([]);
    const [userAnswers, setUserAnswers] = useState({}); // State to track user answers
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(true);
    const [timer, setTimer] = useState(900); // 15 minutes in seconds
    const [score, setScore] = useState(null); // State to hold the calculated score
    const navigate = useNavigate(); // Initialize useNavigate

    // Score submission function wrapped in useCallback
    const handleSubmitScore = useCallback(async () => {
        const totalQuestions = questions.length;
        const correctAnswers = questions.filter((question) => 
            question.correctAnswer === userAnswers[question.id]
        ).length; 
        const calculatedScore = Math.round((correctAnswers / totalQuestions) * 100); 

        // Use the actual user ID from currentUser
        const userId = parseInt(currentUser.id, 10); // Ensure this is a number

        try {
            const response = await fetch('http://localhost:5000/api/test/submit', { // Ensure full URL is used
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    userId: userId, // Ensure this is a number
                    score: calculatedScore,
                }),
            });

            if (!response.ok) {
                throw new Error('Network response was not ok');
            }

            const result = await response.json();
            console.log(result);

            // Set the score state to display it
            setScore(calculatedScore);

            // Navigate to results page after submission
            navigate('/results'); 
        } catch (error) {
            console.error('Error submitting score:', error);
        }
    }, [questions, userAnswers, currentUser, navigate]); // Added dependencies

    useEffect(() => {
        const fetchQuestions = async () => {
            try {
                const response = await getQuestions();
                if (response && response.questions && response.questions.length > 0) {
                    setQuestions(response.questions);
                } else {
                    setError('No questions available');
                }
            } catch (err) {
                console.error('Failed to fetch questions:', err);
                setError('Failed to fetch questions');
            } finally {
                setLoading(false);
            }
        };

        fetchQuestions();
    }, []);

    useEffect(() => {
        // Timer functionality
        const countdown = setInterval(() => {
            setTimer((prevTimer) => {
                if (prevTimer <= 1) {
                    clearInterval(countdown);
                    handleSubmitScore(); // Submit the test when time runs out
                    return 0;
                }
                return prevTimer - 1;
            });
        }, 1000);

        return () => clearInterval(countdown);
    }, [handleSubmitScore]); // Include handleSubmitScore in the dependency array

    const handleAnswerChange = (questionId, answer) => {
        setUserAnswers((prevAnswers) => ({
            ...prevAnswers,
            [questionId]: answer,
        }));
    };

    if (loading) {
        return <div>Loading questions...</div>;
    }

    if (error) {
        return <div>{error}</div>;
    }

    return (
        <div>
            <h2>Time Left: {Math.floor(timer / 60)}:{String(timer % 60).padStart(2, '0')}</h2>
            {questions.map((question, index) => (
                <div key={question.id}>
                    <h4>{question.text}</h4>
                    <div>
                        <input 
                            type="radio" 
                            name={`question${index}`} 
                            value="1" 
                            onChange={() => handleAnswerChange(question.id, '1')} 
                        /> {question.option1}<br />
                        <input 
                            type="radio" 
                            name={`question${index}`} 
                            value="2" 
                            onChange={() => handleAnswerChange(question.id, '2')} 
                        /> {question.option2}<br />
                        <input 
                            type="radio" 
                            name={`question${index}`} 
                            value="3" 
                            onChange={() => handleAnswerChange(question.id, '3')} 
                        /> {question.option3}<br />
                        <input 
                            type="radio" 
                            name={`question${index}`} 
                            value="4" 
                            onChange={() => handleAnswerChange(question.id, '4')} 
                        /> {question.option4}<br />
                    </div>
                </div>
            ))}
            <button onClick={handleSubmitScore}>Submit Test</button>
            {score !== null && <h3>Your Score: {score}%</h3>} {/* Display score if available */}
        </div>
    );
};

export default Test;
