import React, { useEffect, useState, useCallback } from 'react';
import { getQuestions } from '../api'; // Import getQuestions function
import { useNavigate } from 'react-router-dom'; // Import useNavigate for navigation

const Test = () => {
    const [questions, setQuestions] = useState([]);
    const [userAnswers, setUserAnswers] = useState({}); // State to track user answers
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(true);
    const [timer, setTimer] = useState(900); // 15 minutes in seconds
    const navigate = useNavigate(); // Initialize useNavigate

    // Replace 'USER_ID' with actual user ID retrieval logic
    const currentUser = { id: 1 }; // Placeholder for actual user ID

    const handleSubmit = useCallback(() => {
        const totalQuestions = questions.length;
        const correctAnswers = questions.filter((question) => 
            question.correctAnswer === userAnswers[question.id]
        ).length; // Assuming each question has a 'correctAnswer' property

        const calculatedScore = Math.round((correctAnswers / totalQuestions) * 100); // Calculate score

        // Navigate to Score page with calculated score and user ID
        navigate('/score', {
            state: { score: calculatedScore, userId: currentUser.id },
        });
    }, [questions, userAnswers, currentUser.id, navigate]); // Add dependencies

    useEffect(() => {
        const fetchQuestions = async () => {
            try {
                const response = await getQuestions();
                if (response && response.questions && response.questions.length > 0) {
                    setQuestions(response.questions);
                } else {
                    setError('No questions available');
                }
            } catch (err) {
                console.error('Failed to fetch questions:', err);
                setError('Failed to fetch questions');
            } finally {
                setLoading(false);
            }
        };

        fetchQuestions();
    }, []);

    useEffect(() => {
        // Timer functionality
        const countdown = setInterval(() => {
            setTimer((prevTimer) => {
                if (prevTimer <= 1) {
                    clearInterval(countdown);
                    handleSubmit(); // Submit the test when time runs out
                    return 0;
                }
                return prevTimer - 1;
            });
        }, 1000);

        return () => clearInterval(countdown);
    }, [handleSubmit]); // Added handleSubmit to dependencies

    const handleAnswerChange = (questionId, answer) => {
        setUserAnswers((prevAnswers) => ({
            ...prevAnswers,
            [questionId]: answer,
        }));
    };

    if (loading) {
        return <div>Loading questions...</div>;
    }

    if (error) {
        return <div>{error}</div>;
    }

    return (
        <div>
            <h2>Time Left: {Math.floor(timer / 60)}:{String(timer % 60).padStart(2, '0')}</h2>
            {questions.map((question, index) => (
                <div key={question.id}>
                    <h4>{question.text}</h4>
                    <div>
                        <input 
                            type="radio" 
                            name={`question${index}`} 
                            value="1" 
                            onChange={() => handleAnswerChange(question.id, '1')} 
                        /> {question.option1}<br />
                        <input 
                            type="radio" 
                            name={`question${index}`} 
                            value="2" 
                            onChange={() => handleAnswerChange(question.id, '2')} 
                        /> {question.option2}<br />
                        <input 
                            type="radio" 
                            name={`question${index}`} 
                            value="3" 
                            onChange={() => handleAnswerChange(question.id, '3')} 
                        /> {question.option3}<br />
                        <input 
                            type="radio" 
                            name={`question${index}`} 
                            value="4" 
                            onChange={() => handleAnswerChange(question.id, '4')} 
                        /> {question.option4}<br />
                    </div>
                </div>
            ))}
            <button onClick={handleSubmit}>Submit Test</button>
        </div>
    );
};

export default Test;
