// src/api.js
import axios from 'axios';

const API_URL = 'http://localhost:5000/api'; // Adjust this to your server URL

export const registerUser = async (userData) => {
    const response = await axios.post('http://localhost:5000/api/auth/register', userData);
    return response.data; // Return the response data for handling
};

export const loginUser = async (credentials) => {
    try {
        const response = await axios.post('http://localhost:5000/api/auth/login', credentials);
        return response.data;
    } catch (error) {
        console.error('Login error:', error.response ? error.response.data : error.message);
        throw error; // Re-throw the error after logging it
    }
};

export const getQuestions = async () => {
    try {
        const response = await axios.get('/api/questions/random'); // Adjust the URL based on your backend
        return response.data; // Ensure this matches the structure of your backend response
    } catch (error) {
        throw new Error('Error fetching questions');
    }
};

export const submitTest = async (data) => {
    return await axios.post(`${API_URL}/test/submit`, data);
};
