// src/api.js
import axios from 'axios';

const API_URL = 'http://localhost:5000/api'; // Adjust this to your server URL

export const registerUser = async (userData) => {
    const response = await axios.post('http://localhost:5000/api/auth/register', userData);
    return response.data; // Return the response data for handling
};

export const loginUser = async (data) => {
    return await axios.post(`${API_URL}/auth/login`, data);
};

export const fetchQuestions = async () => {
    return await axios.get(`${API_URL}/questions`);
};

export const submitTest = async (data) => {
    return await axios.post(`${API_URL}/test/submit`, data);
};
