import React, { useEffect, useState } from 'react';
import { getQuestions } from '../api';

const Test = () => {
    const [questions, setQuestions] = useState([]);
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(true);
    const [timer, setTimer] = useState(900); // 15 minutes in seconds

    useEffect(() => {
        const fetchQuestions = async () => {
            try {
                const response = await getQuestions();
                if (response && response.questions && response.questions.length > 0) {
                    setQuestions(response.questions);
                } else {
                    setError('No questions available');
                }
            } catch (err) {
                console.error('Failed to fetch questions:', err);
                setError('Failed to fetch questions');
            } finally {
                setLoading(false);
            }
        };

        fetchQuestions();
    }, []);

    useEffect(() => {
        // Timer functionality
        const countdown = setInterval(() => {
            setTimer((prevTimer) => {
                if (prevTimer <= 1) {
                    clearInterval(countdown);
                    // Here you could handle what happens when time runs out, e.g., submit answers
                    return 0;
                }
                return prevTimer - 1;
            });
        }, 1000);

        return () => clearInterval(countdown);
    }, []);

    if (loading) {
        return <div>Loading questions...</div>;
    }

    if (error) {
        return <div>{error}</div>;
    }

    return (
        <div>
            <h2>Time Left: {Math.floor(timer / 60)}:{String(timer % 60).padStart(2, '0')}</h2>
            {questions.map((question, index) => (
                <div key={question.id}>
                    <h4>{question.text}</h4>
                    <div>
                        <input type="radio" name={`question${index}`} value="1" /> {question.option1}<br />
                        <input type="radio" name={`question${index}`} value="2" /> {question.option2}<br />
                        <input type="radio" name={`question${index}`} value="3" /> {question.option3}<br />
                        <input type="radio" name={`question${index}`} value="4" /> {question.option4}<br />
                    </div>
                </div>
            ))}
        </div>
    );
};

export default Test;
